import React from "react";
import {
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from "@mui/material";
import "./index.css"; // Import the CSS file

const InvoiceGrid = ({ formState }) => {
  return (
    <TableContainer>
      <Table
        style={{ minWidth: 650, borderCollapse: "collapse", marginTop: "20px" }}
      >
        <TableHead style={{ backgroundColor: "#f2f2f2" }}>
          <TableRow>
            <TableCell className="grid-container">ID</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Qty</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Price</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Discount %</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Tax %</TableCell>
            <TableCell style={{ fontWeight: "bold" }}>Total Price</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {formState.map((invoice, index) => (
            <TableRow key={index} style={{ borderBottom: "1px solid #ddd" }}>
              <TableCell>{index + 1}</TableCell>
              <TableCell>{invoice.qty}</TableCell>
              <TableCell>{invoice.price}</TableCell>
              <TableCell>{invoice.discountPercent}</TableCell>
              <TableCell>{invoice.taxPercent}</TableCell>
              <TableCell>{invoice.total}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default InvoiceGrid;
