import React, { useState } from "react";
import { TextField, Button } from "@mui/material";

const InvoiceForm = ({ addInvoice }) => {
  const [formState, setFormState] = useState({
    qty: 0,
    price: 0,
    discountPercent: 0,
    taxPercent: 0,
  });

  const calculateDiscount = () => {
    return (formState.qty * formState.price * formState.discountPercent) / 100;
  };

  const calculateTax = () => {
    return (formState.qty * formState.price * formState.taxPercent) / 100;
  };

  const calculateTotalPrice = () => {
    return (
      formState.qty * formState.price - calculateDiscount() + calculateTax()
    );
  };

  const handleSubmit = () => {
    const newInvoice = {
      id: Date.now(), // Using timestamp as a simple unique identifier
      ...formState,
      totalPrice: calculateTotalPrice(),
    };

    addInvoice(newInvoice);
    // Reset form state after submission
    setFormState({
      qty: 0,
      price: 0,
      discountPercent: 0,
      taxPercent: 0,
    });
  };

  return (
    <div>
      <form>
        {/* ... other form fields ... */}
        <div>
          <strong>Total Price:</strong> {calculateTotalPrice()}
        </div>
        <Button variant="contained" color="primary" onClick={handleSubmit}>
          Submit
        </Button>
      </form>
    </div>
  );
};

export default InvoiceForm;
