import React, { useState } from "react";
import { TextField, Button } from "@mui/material";
import InvoiceGrid from "./InvoiceGrid";

const InvoiceForm = () => {
  const [formState, setFormState] = useState({
    qty: 0,
    price: 0,
    discountPercent: 0,
    taxPercent: 0,
  });

  const [submit, setSubmit] = useState([]);

  const calculateDiscount = () => {
    return (formState.qty * formState.price * formState.discountPercent) / 100;
  };

  const calculateTax = () => {
    return (formState.qty * formState.price * formState.taxPercent) / 100;
  };

  const calculateTotalPrice = () => {
    return (
      formState.qty * formState.price - calculateDiscount() + calculateTax()
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const submittedItem = {
      qty: formState.qty,
      price: formState.price,
      discountPercent: formState.discountPercent,
      taxPercent: formState.taxPercent,
      total: calculateTotalPrice(),
    };

    setSubmit((prevSubmit) => [...prevSubmit, submittedItem]);
    setFormState({
      qty: 0,
      price: 0,
      discountPercent: 0,
      taxPercent: 0,
    });
  };

  return (
    <div>
      <form>
        <TextField
          label="Qty"
          type="number"
          value={formState.qty}
          onChange={(e) => setFormState({ ...formState, qty: e.target.value })}
        />
        <TextField
          label="Price"
          type="number"
          value={formState.price}
          onChange={(e) =>
            setFormState({ ...formState, price: e.target.value })
          }
        />
        <TextField
          label="Discount %"
          type="number"
          value={formState.discountPercent}
          onChange={(e) =>
            setFormState({ ...formState, discountPercent: e.target.value })
          }
        />
        <TextField
          label="Tax %"
          type="number"
          value={formState.taxPercent}
          onChange={(e) =>
            setFormState({ ...formState, taxPercent: e.target.value })
          }
        />
        <div>
          <strong>Total Price:</strong> {calculateTotalPrice()}
        </div>
        <Button variant="contained" color="primary" onClick={handleSubmit}>
          Submit
        </Button>
      </form>

      <div
        style={{
          padding: "30px",
        }}
      >
        <InvoiceGrid formState={submit} />
      </div>
    </div>
  );
};

export default InvoiceForm;
