import React, { useState } from "react";
import { TextField, Button, Box } from "@mui/material";
import InvoiceGrid from "./InvoiceGrid";

const defaultValue = {
  qty: 0,
  price: 0,
  discountPercent: 0,
  taxPercent: 0,
};

const InvoiceForm = () => {
  const [formState, setFormState] = useState({
    ...defaultValue,
  });

  const [submit, setSubmit] = useState([]);

  const calculateDiscount = () => {
    return (formState.qty * formState.price * formState.discountPercent) / 100;
  };

  const calculateTax = () => {
    return (formState.qty * formState.price * formState.taxPercent) / 100;
  };

  const calculateTotalPrice = () => {
    return (
      formState.qty * formState.price - calculateDiscount() + calculateTax()
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const submittedItem = {
      qty: formState.qty,
      price: formState.price,
      discountPercent: formState.discountPercent,
      taxPercent: formState.taxPercent,
      total: calculateTotalPrice(),
    };

    setSubmit((prevSubmit) => [...prevSubmit, submittedItem]);
    setFormState({
      ...defaultValue,
    });
  };

  return (
    <>
      <Box style={{ maxWidth: "600px", margin: "auto", padding: "20px" }}>
        <form style={{ display: "flex", flexDirection: "column" }}>
          <TextField
            label="Qty"
            type="number"
            value={formState.qty}
            onChange={(e) =>
              setFormState({ ...formState, qty: e.target.value })
            }
            style={{ marginBottom: "10px" }}
          />
          <TextField
            label="Price"
            type="number"
            value={formState.price}
            onChange={(e) =>
              setFormState({ ...formState, price: e.target.value })
            }
            style={{ marginBottom: "10px" }}
          />
          <TextField
            label="Discount %"
            type="number"
            value={formState.discountPercent}
            onChange={(e) =>
              setFormState({ ...formState, discountPercent: e.target.value })
            }
            style={{ marginBottom: "10px" }}
          />
          <TextField
            label="Tax %"
            type="number"
            value={formState.taxPercent}
            onChange={(e) =>
              setFormState({ ...formState, taxPercent: e.target.value })
            }
            style={{ marginBottom: "10px" }}
          />
          <Box style={{ marginBottom: "20px" }}>
            <strong>Total Price:</strong> {calculateTotalPrice()}
          </Box>
          <Button
            variant="contained"
            color="primary"
            onClick={handleSubmit}
            style={{ width: "120px", alignSelf: "center" }}
          >
            Submit
          </Button>
        </form>
      </Box>
      <Box
        style={{
          padding: "30px",
        }}
      >
        <InvoiceGrid formState={submit} />
      </Box>
    </>
  );
};

export default InvoiceForm;
