// InvoiceGrid.js
import React, { useState } from "react";
import {
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  TablePagination,
} from "@mui/material";
import "./index.css";

const InvoiceGrid = ({ formState }) => {
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5); // Set the number of rows per page

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  console.log("Koca: formState ", formState);

  return (
    <TableContainer>
      <Table
        style={{ minWidth: 650, borderCollapse: "collapse", marginTop: "20px" }}
      >
        <TableHead style={{ backgroundColor: "#f2f2f2" }}>
          <TableRow>
            <TableCell sx={{ fontWeight: "bold" }}>No</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Qty</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Price</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Discount %</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Discounted Price</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Tax %</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Tax Price</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Total Amount</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {(rowsPerPage > 0
            ? formState.slice(
                page * rowsPerPage,
                page * rowsPerPage + rowsPerPage
              )
            : formState
          ).map((invoice, index) => (
            <TableRow key={index} style={{ borderBottom: "1px solid #ddd" }}>
              <TableCell>{index + 1 + page * rowsPerPage}</TableCell>
              <TableCell>{invoice.qty}</TableCell>
              <TableCell>{invoice.price}</TableCell>
              <TableCell>{invoice.discountPercent}</TableCell>
              <TableCell>{invoice.discount}</TableCell>
              <TableCell>{invoice.taxPercent}</TableCell>
              <TableCell>{invoice.tax}</TableCell>
              <TableCell sx={{ fontWeight: "bold" }}>
                {invoice.totalPrice} ₹
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[5, 10, 25, { label: "All", value: -1 }]}
        component="div"
        count={formState.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
    </TableContainer>
  );
};

export default InvoiceGrid;
