import React from "react";
import {
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  IconButton,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import "./index.css";

const InvoiceGrid = ({ formState, handleEdit }) => {
  return (
    <TableContainer>
      <Table
        style={{ minWidth: 650, borderCollapse: "collapse", marginTop: "20px" }}
      >
        <TableHead style={{ backgroundColor: "#f2f2f2" }}>
          <TableRow>
            {/* ... (existing code) */}
            <TableCell sx={{ fontWeight: "bold" }}>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {formState.map((invoice, index) => (
            <TableRow key={index} style={{ borderBottom: "1px solid #ddd" }}>
              {/* ... (existing code) */}
              <TableCell>
                <IconButton onClick={() => handleEdit(index)}>
                  <EditIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default InvoiceGrid;
